import operator

text = ''''''

'''YEQCCQMNCKRQLTBKRTKPTEAQKRBVKNBKRQFVSBCQEVXKRQSBJVMEIBVWCKTBMQKRN'''
"""BLESSEDISTHEMANTHATWALRETHNOTINTHECOUNSELOFTHEUNGODLY"""

alphab2t = 'TYFMQXJRN AELBV  WCKS P I '
alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'
#alphab1t = ' Y   Q RN    BV  W K      '

frecv_monogram = {}
frecv_bigram = {}

def fill_in():
    for i in range(len(text) - 1):
        bigram = text[i : i + 2]
        if bigram in frecv_bigram:
            frecv_bigram[bigram] += 1
        else:
            frecv_bigram[bigram] = 1
        monogram = text[i]
        if monogram in frecv_monogram:
            frecv_monogram[monogram] += 1
        else:
            frecv_monogram[monogram] = 1

def sort_dictionary(d):
    sorted_dict = list(reversed(sorted(d.items(), key=operator.itemgetter(1))))
    return sorted_dict

if __name__ == '__main__':
    fill_in()
